 function HannesRoest() { //14Xyz45
    w (0,trenner,'Hannes Röst','');

    w (1,'/wiki/User:Hannes_Röst/Notes','Notes',qbtarget,'Hannes Röst Notes');
    w (1,'/wiki/User:Hannes_Röst/Bench','Bench',qbtarget,'Hannes Röst Bench');
   // w (1,'/wiki/Benutzer:Hannes_Röst/Beobachtete','Beobachtete',qbtarget,'Hannes Röst Beobachtete');
   // w (1,'/wiki/Benutzer:Hannes_Röst/Werkzeuge','Werkzeuge',qbtarget,'Hannes Röst Werkzeuge');
   // w (1,'/wiki/Benutzer:Hannes_Röst/Beiträge','Beiträge',qbtarget,'Hannes Röst Beiträge');
   // w (1,'/wiki/Benutzer:Hannes_Röst/Mentor','Mentor',qbtarget,'Hannes Röst Mentor');
    w (1,"http://de.wikipedia.org/wiki/Benutzer:Hannes_Röst",'de',qbtarget,'de Wikiseite');

    w (1,"http://de.wikipedia.org/wiki/Spezial:Seiten_mit_ungesichteten_Versionen",'unges',qbtarget,'Seiten_mit_ungesichteten_Versionen');

    w (1,"http://de.wikipedia.org/wiki/Spezial:Beitr%C3%A4ge/HRoestBot",'HRoestBot',qbtarget,'Beiträge von HRoestBot');

 }

