: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-7}}</nowiki></tt> returns {{MONTHNUMBER|-7}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-6}}</nowiki></tt> returns {{MONTHNUMBER|-6}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-5}}</nowiki></tt> returns {{MONTHNUMBER|-5}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-4}}</nowiki></tt> returns {{MONTHNUMBER|-4}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-3}}</nowiki></tt> returns {{MONTHNUMBER|-3}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-2}}</nowiki></tt> returns {{MONTHNUMBER|-2}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-1}}</nowiki></tt> returns {{MONTHNUMBER|-1}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-0.9999}}</nowiki></tt> returns {{MONTHNUMBER|-0.9999}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-0.5}}</nowiki></tt> returns {{MONTHNUMBER|-0.5}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|-0.0001}}</nowiki></tt> returns {{MONTHNUMBER|-0.0001}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|0}}</nowiki></tt> returns {{MONTHNUMBER|0}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|0.0001}}</nowiki></tt> returns {{MONTHNUMBER|0.0001}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|0.5}}</nowiki></tt> returns {{MONTHNUMBER|0}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|0.9999}}</nowiki></tt> returns {{MONTHNUMBER|0.9999}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|12.0001}}</nowiki></tt> returns {{MONTHNUMBER|12.0001}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|12.5}}</nowiki></tt> returns {{MONTHNUMBER|12.5}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|12.9999}}</nowiki></tt> returns {{MONTHNUMBER|12.9999}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|13}}</nowiki></tt> returns {{MONTHNUMBER|13}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|14}}</nowiki></tt> returns {{MONTHNUMBER|14}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|24}}</nowiki></tt> returns {{MONTHNUMBER|24}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|1200}}</nowiki></tt> returns {{MONTHNUMBER|1200}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|1201}}</nowiki></tt> returns {{MONTHNUMBER|1201}}
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|1212}}</nowiki></tt> returns {{MONTHNUMBER|1212}}

; Example for invalid month name<nowiki>:</nowiki>
: <tt><nowiki>{{</nowiki>MONTHNUMBER<nowiki>|xxx}}</nowiki></tt> returns {{MONTHNUMBER|xxx}}

