This hook allows you to add up to ten optional categories, that will be added to a page if a particular parameter is defined.  Each category will be added if the corresponding parameter is non-empty. This template displays no visible output, so it can be put anywhere in the template code; common practice is to put it at the end.
<includeonly>
[[Category:Template subpages|{{PAGENAME}}]]
</includeonly>

{{WPBannerMeta
|PROJECT              = Languages
 |BANNER_NAME         = Template:WikiProject Languages
 |substcheck=<includeonly>{{subst:</includeonly><includeonly>substcheck}}</includeonly>
 |small               = {{{small|}}}
 |category            = {{{category|}}}
 |listas              = {{{listas|}}}
|IMAGE_LEFT           = Globe of letters.svg
 |IMAGE_LEFT_SMALL    = 
 |IMAGE_LEFT_LARGE    = 55px
|QUALITY_SCALE        = extended
 |quality             = {{{quality|}}}
|PRIORITY_SCALE       = standard
 |priority            = {{{priority|}}}
|ASSESSMENT_CAT       = language pages
|ASSESSMENT_LINK      = Wikibooks:WikiProject Languages/Assessment
|SUBJECT              = Languages
|attention            = {{{attention|}}}
 |ATTENTION_CAT       = Language pages needing attention
|note 1               = {{{needs-bite|}}}
 |NOTE_1_TEXT         = This page has been marked as needing sound bite from a native speaker.
 |NOTE_1_IMAGE        = Crystal Clear app kaboodle.png
 |NOTE_1_CAT          = Language pages needing sound bites

|HOOK_TF = {{WPBannerMeta/hooks/taskforces
 |category={{{category|¬}}}
 |BANNER_NAME            = Template:WikiProject Languages
 |QUALITY_SCALE          = extended
 |quality                = {{{quality|}}}
 |PRIORITY_SCALE         = standard

 |tf 1              = {{{french|}}}
  |TF_1_LINK        = Wikibooks:WikiProject Languages/French language task force
  |TF_1_NAME        = the French language task force
  |TF_1_IMAGE       = Flag of France.svg
  |TF_1_QUALITY     = yes
  |tf 1 priority    = {{{french-priority|{{{priority|}}}}}}
  |TF_1_ASSESSMENT_CAT = French language pages
  |TF_1_HOOK        = {{WPBannerMeta/hooks/cats
   |category        = {{{category|¬}}}
   |BANNER_NAME     = Template:WikiProject Languages
   |cat 1           = {{{attention|}}}
    |CAT_1          = French language pages needing attention
   |cat 2           = {{{needs-bite|}}}
    |CAT_2          = French language pages needing sound bites
  }} <!-- WPBannerMeta/hooks/cats -->

 |tf 2              = {{{german|}}}
  |TF_2_LINK        = Wikibooks:WikiProject Languages/German language task force
  |TF_2_NAME        = the German language task force
  |TF_2_IMAGE       = Flag of Germany.svg
  |TF_2_QUALITY     = yes
  |tf 2 priority    = {{{german-priority|{{{priority|}}}}}}
  |TF_2_ASSESSMENT_CAT = German language pages
  |TF_2_HOOK        = {{WPBannerMeta/hooks/cats
   |category        = {{{category|¬}}}
   |BANNER_NAME     = Template:WikiProject Languages
   |cat 1           = {{{attention|}}}
    |CAT_1          = German language pages needing attention
   |cat 2           = {{{needs-bite|}}}
    |CAT_2          = German language pages needing sound bites
  }} <!-- WPBannerMeta/hooks/cats -->

 |tf 3              = {{{spanish|}}}
  |TF_3_LINK        = Wikibooks:WikiProject Languages/Spanish language task force
  |TF_3_NAME        = the Spanish language task force
  |TF_3_IMAGE       = Flag of Mexico.svg
  |TF_3_QUALITY     = yes
  |tf 3 priority    = {{{spanish-priority|{{{priority|}}}}}}
