{
  document.body.style.cursor = "wait";
  api=JsMwApi();
  api({action:"query", list:"allpages", apprefix:wgPageName+"/", aplimit:150},function(res){
    var q=res.query.allpages;
    var l=q.length;
    for(var i=0; i < q.length; i++){
      api({action: "watch", title: q[i].title, unwatch:unwatch},function(){
        if(!--l)document.body.style.cursor = null;
        })
      }
    })
}

