  String getSuit(int suitNo) {
    String suit = "";
    switch (suitNo) {
      case 1:
        suit = "Clubs";
        break;
      case 2:
        suit = "Diamonds";
        break;
      case 3:
        suit = "Hearts";
        break;
      case 4:
        suit = "Spades";
        break;
      case 5:
/* add an extra suit to the deck.txt (5 1 5 2 5 3 ect)
*  every instance in the code where 51 or 52 appears +13
* to accommodate the new suit. */
    	 suit= "Swords";
    	 break;
    }
    return suit;
  }
