  boolean isNextCardHigher(TCard lastCard, TCard nextCard){
    boolean higher;
    higher = false;

    if (nextCard.rank > lastCard.rank){

    		 higher=true;

    }

    if(nextCard.rank==lastCard.rank){
   	 if (nextCard.suit < lastCard.suit){ 
         higher = false;
     	 }
     	 else if(nextCard.suit > lastCard.suit){
     		 higher=true;
     	 }
    }
    return higher;

  }
