Function IsNextCardHigher(LastCard, NextCard : TCard) : Boolean;
  Var
    Higher : Boolean;
  Begin
    Higher := False;
    If NextCard.Rank > LastCard.Rank
      Then Higher := True;
    If NextCard.Rank = LastCard.Rank then
    begin
      If NextCard.Suit > LastCard.Suit then
        Higher :=true;
    end;
    IsNextCardHigher := Higher;
  End;
