        If averagescore > 0 Then
            averagescore = ((averagescore * (gamesplayed - 1)) + Score) / gamesplayed
            Console.WriteLine("Average Score: " & averagescore)
        Else
            averagescore = Score / gamesplayed
            Console.WriteLine("Average Score: " & averagescore)
        End If

    End Sub

