Solution #2
<source lang=cpp>
#include <iostream>
#inlcude <sstream>
using namespace std;

int main(void)
{
  string swap;
  int a, b;
  cout << "Input a: ";
  cin >> swap;
  while ( !(stringstream(swap) >> a) )
  {
    cout << "Error! Type mismatch! \nInput again: ";
    cin >> swap;
  }

  cout << "Input b: ";
  cin >> swap;
  while ( !(stringstream(swap) >> b) )
  {
    cout << "Error! Type mismatch! \nInput again: ";
    cin >> swap;
  }

  a ^= b ^= a ^=b;
  cout << "a = " << a << endl;
  cout << "b = " << b << endl;
}
