<source lang=cpp>
//By Zhen
#include <stdio.h>

void Swap(int&, int&);

int main(){
	int a, b;
	printf("Input the first integer: ");
	scanf("%i", &a);

	printf("Input the second integer: ");
	scanf("%i", &b);

	Swap(a, b);

	printf("Integer #1 is now: %i and Integer #2 is now: %i\n", a, b);
	return 0;
}

void Swap(int& a, int& b){
	int c;
	c = a;
	a = b;
	b = c;
}
