<source lang=cpp>
// By Davidov

#include <iostream>

using namespace std;

int main()
{
    int a, b;

    cout << "A: ";
    cin >> a;
    cout << "B: ";
    cin >> b;

    // this is simillar to the xor encryption....
    // It'd be nice if you catch the trick
    // The good thing:
    //    It's quick, no extra memory, no info loss

    b = a ^ b;
    a = a ^ b;
    b = a ^ b;

    cout << "A = " << a <<endl;
    cout << "B = " << b << endl;

	return 0;
}
