//by Pak S.
#include<iostream>
using namespace std;
int swap(int&a, int&b)
{
	cout<<" Enter the first number "<<endl;
	cin>>a;
	cout<<" Enter the second number "<<endl;
	cin>>b;

	int tfirst;
	tfirst = a;
	a = b;
	b = tfirst;
	cout<<a<<"   "<<b<<endl;
	return(a, b);

}

int main()
{
	int a, b;
	swap(a,b);
	return 0;
}
