/*EXERCISE 2
Write a program that asks the user to type all the integers between 8 and 23 (both included) using a for loop. */
#include "stdafx.h"
#include <iostream>
#include <string>
using namespace std;

int main(){

	cout << "Insert numbers from 8 to 23, both included.\n";
	for (int i = 8; i <= 23; ++i){
		string suffix;
		if (i == 8)
			suffix.assign("st");
		else if (i == 9)
			suffix.assign("nd");
		else if (i == 10)
			suffix.assign("rd");
		else
			suffix.assign("th");

		cout << "Insert the " << i - 7 << suffix << " number.\n";
		int inp;
		cin >> inp;
		if (inp != i){
			do {
				cout << "Wrong answer, you must enter: " << i << "\n";
				cin >> inp;
			} while (inp != i);
		}else{}
	}
	cout << "You did it!\n";
	system("pause");
}
