#include <iostream>
using namespace std;

int main() {
    short int input, i(8); // Input and loop variables.

    while (i <= 23) {
        cout << "Enter the number " << i << ": ";
        cin >> input;
        i += (input == i); // Only add to the loop if input is correct
    }

    return(0);
}
