/*EXERCISE 3
~asdfk
Write a program that asks the user to type all the integers between 8 and 23 (both included) using a "while" loop.
*/

#include <iostream>
using namespace std;

void main() {
	int num, next(8);
	while (next < 25) {
		cout << "Please enter a number: ";
		cin >> num;
		if (num == next) {
			next++;
		}else {
			cout << "Wrong input, actual number: " << next << endl;
			system("pause");
			return;
		}
	}
	cout << "Congratulations\n";
	system("pause");
}
