<source lang="cpp">
/* Easy Max and Min solution. Ankit Nayak*/

#include <iostream>
using namespace std;

int main(){
    int max, min;
    max=23;
    min=8;
    int capture;
    int interval=0;
    while(interval<(max-min+1)){
        cout<<"Enter number 8 till 23 serially:  ";
        cin>> capture;
        if(capture==(min+interval)){
            cout<<"Correct entry"<<endl;
            interval++;
        }
        else{
            cout<<"Incorrect entry. Please enter "<<(min+interval)<<endl;
        }
    }
}
