/* A program to sum 10 integers using a loop */

#include <iostream>
using namespace std;

int main()
{
    //Define variables
    int i = 1;              //Counter
    int n = 0;              //Sum
    int temp;               //Input store

    cout << "This program sums ten user entered integers\n\n";

    for (i = 1 ; i <= 10 ; i++) {
        cout << "Type integer " << i << ": ";
        cin >> temp;
        n = n + temp;
    }

    cout << "\nThe sum of the integers is: " << n << endl;

    return 0;
}

