<source lang="cpp">
#include <iostream>
#include <vector>
using namespace std;

int main() {
	vector<int> numbers;
	do {
		short int input;
		static short int count = 1;
		cout << "Enter the " << count << "(st/nd/rd/th) value: ";
		if (!(cin >> input)) {
			cin.clear();
			cin.ignore(10000, '\n');
		} else {
			numbers.push_back(input);
			count += 1;
		}
	} while (numbers.size() != 10);
	int total;
	for(int & n : numbers) {
		total += n;
	}
	cout << total << endl;
	return 0;
}
