using std::cin;
using std::cout;
using std::vector;

int main()
{
    double input;
    vector <double> vec;
    cout << "Enter 10 separate integers:\n";

    for (int i = 0; i < 10; ++i) {
        cin >> input;
        vec.push_back(input);
    }

    sort(vec.begin(), vec.end());

    cout << "Smallest value is " << vec[0];

    return 0;
