#include <iostream>
#include <cmath>
using namespace std;

int main() {
	int a,i,sum=pow(5,3);
	cout<<"Enter an integer greater than 5: ";
	cin>>a;
	i=a-5;
	while (i<=a) {
		sum+=pow(i,3);
		i++;
