#include <iostream>
using namespace std;
int main()
{
  int num, sum = 0;
  cout << "Enter a number from 1-10: "; cin >> num;

  if (num < 5)
  {
    for(; num < 5; num++)
      sum =sum + num * num * num;
  }
  else if (num > 5)
  {
    for(; num > 5; num--)
      sum = sum + num * num * num;
  }
  cout << sum + 125 << endl;
  return 0;
}
