<source lang="cpp">
/*
EXCERCISE 6
Write a program that asks the user to type an integer N 
and computes the sum of the cubes for all integer numbers from 53 to N3.
*/

#include <iostream>

using namespace std;

void main() {
	int N, i(5), result(0);
	cout << "Number please: ";
	cin >> N;
	if (N > i) {
		for (; i <= N ; i++) {
			result += i*i*i;
		}
	} else if (N < i) {
		for (; i >= N ; i--) {
			result += i*i*i;
		}
	} else {
		result = 125;
	}
	cout << "Result: " << result << endl ;
}
</source>
Solution #6
<source lang="cpp">
// computes the sum of the cubes from 5^3 to N^3 ; N is input from user;

#include <iostream>

using namespace std;

const int S=5; // series start or end at 5
int N, result=0;

void case1();
void case2();
void case3();

int main()
{

	// get val of N from user
	cout<<endl<<"Enter an Interger : ";
	cin>>N;

	// three different cases can occur such as :: N>5 or N<5 or N=5
	if(N>S)
		case1();
	else if(N<S)
		case2();
	else if(N=S)
		case3();	
}

void case1()
{
	// N>5
	for(int i=S; i<=N; i++)
		result += (i*i*i);

	for(int i=S; i<=N; i++) //to print result with series
		cout<<"("<<i<<"^3)+";

	cout<<"\b"<<" = "<<result; // "\b" deletes the last "+" from the above loop
}

void case2()
{
	//N<5;
	for(int i=N; i<=S; i++)
		result += (i*i*i);

	for(int i=N; i<=S; i++) //to print result with series
		cout<<"("<<i<<"^3)+";

	cout<<"\b"<<" = "<<result; // "\b" deletes the last "+" from the above loop
}

void case3()
{
	//N=5;
	result = N*N*N;

	cout<<"(5^3) = "<<result;
}


</source>
