<source lang="cpp">
// by J0nDaFr3aK

#include <iostream>
using namespace std;

int sumOfCubes(int n) {
    int sum = 0, i;
    for (i = 5; i <= n; i++) {
        sum += i * i * i;
    }

    return sum;
}

int main()
{
    int n;
    do {
        cout << "enter number ( >= 5 ): ";
        cin >> n;
    }while (n < 5);

    cout << "the sum is " << sumOfCubes(n);

    return 0;
}
