Solution #10
<source lang="cpp">
#include <iostream>

using namespace std;

int main()
{
    cout << "Enter an integer N and I will compute the sum of 5³ to N³." << endl;

    int N = 0;
    cin >> N;

    int count = N > 5 ? 1 : -1, //If N > 5 we count up to N else we count down to N
 	i = 5,			//Startvalue is 5
	sum = N*N*N;		//The sum-total

    for (; i != N; i += count)
	sum += i*i*i;

    cout << "The sum is: " << sum << endl;
}

