//Solution by Aldo Ziflaj
#include <iostream>
#include <cmath>
using namespace std;
int f(int n) {
	if (n==0) return 3;
	else return 3*f(n-1)+4;
}

int main() {
	int a;
	cout<<"Enter an integer: ";
	cin>>a;
	cout<<"u("<<a<<")="<<f(a);
	return 0;
}

