//solution by Tomislav Petrović

#include <iostream>
using namespace std;

int get(int num)
{
    int result;
    switch(num)
        {
        case 0:
            result = 1;
            break;
        case 1:
            result = 1;
            break;
        default:
            result = get(num - 1) + get(num - 2);
            break;
        }
    return result;
}

int main()
{
    int num;
    cout << "type int N: ";
    cin >> num;

    cout << "u(N) = " << get(num);

    return 0;
}
