<source lang="cpp">
#include <iostream>

using namespace std;

int main()
{
	int n, op;
	bool quit = false;

	while( !quit && (cin >> n) ) //get input if 'quit' is false
	{
		cout << "What do you want to do with " << n << '?' << endl
			 << "1. Add 1" << endl
			 << "2. Multiply by 2" << endl
			 << "3. Subtract 4" << endl
			 << "4. Quit" << endl;
		cin >> op;

		switch(op)
		{
			case 1:
				cout << n + 1 << endl;
			break;
			case 2:
				cout << n * 2 << endl;
			break;
			case 3:
				cout << n - 4 << endl;
			break;
			case 4:
				quit = true;
			break;
			default:
				cout << "Unknown operation!" << endl;
			break;
		}
	}

	return 0;
}

