    if (a==1){ 
       b=b+1;
       cout << "The result is " << b; 
       }
    else if (a==2) {
       b=b*2;
       cout << "The result is " << b;
       }
    else if (a==3) {
       b=b-4;
       cout << "The result is " << b; }
    else if (a==4) {
