		{
		case 1 : b=n+1;break;
		case 2 : b=n*2;break;
		case 3 : b=n-4;break;
		}
	if (i!= 4)
		{
		cout << "result: " << b<<endl;
		system("pause");
		system("cls");
		}

	}
		return 0;
}
