// EXERCISE 11 - Solution 2

#include <iostream>

using namespace std;

void main() {
	int num, i(0);
	double sum(0);
		cout << "Type a number, type 0 to stop: ";
		cin >> num;
	while (num != 0) {
		cout << "Number " << i + 2 << ": ";
		cin >> num;
		if (num < 0) {
			cout << "ERROR";
			return;
		} else if (num != 0) {
		sum += num;
		i++;
		}
	}
	if (sum != 0)
		cout << "Average: " << sum / i;
	else
		cout << "No average";
}


// EXERCISE 11 - Solution 3

#include <iostream>
#include <vector>
#include <cstdlib>

using namespace std;

int main()
{
    vector<int> numList;
    vector<int>::const_iterator iter;

    int num, total(0), average(0);

    do
    {
        cout << "Enter a whole number to add to list, '0' to get average.";
        cin >> num;

        if (num < 0)
            cout << "ERROR\n";
        else if (num > 0)
            numList.push_back(num);
        else;
    } while (num != 0);

    for (iter = numList.begin(); iter != numList.end(); iter++)
        total += *iter;

    if (total == 0)
        cout << "NO AVERAGE\n";

    average = (total / numList.size());
    cout << "Average is " << average;

    return 0;
}

// EXERCISE 11 - Solution 4

#include <iostream>

#define ERR_NONE	0;

using namespace std;

int main(int argc, char** argv)
{
	int iInput = 0;
	int iIndex = 0;
	int n = 0;
	cout << "Please type a list of positive integers to average. (0 When list is complete):" << endl;		
	do
	{
		cin >> iInput;
		if (iInput < 0)
		{
			cout << "ERROR! Integer must be positive." << endl;
		}
		else if (iInput > 0)
		{
			n+=iInput;
			iIndex++;
		}
	}while (iInput!=0);

	if (iIndex > 0)
	{
		cout << "The average is " << n/iIndex << endl;
	}

return ERR_NONE;
}

