<source lang="cpp">
// by blazzer12

#include <iostream>

using namespace std;

int u(int n)
{
	if(n==0)
		return 3;
	else if(n==1)
		return 2;
	else
		return n * u(n-1) + (n+1) * u(n-2) +n;
} 

int main()
{
	int n;

	cout<<"This program will compute the equation:"<<endl<<"\t\tn * u(n-1) + (n+1) * u(n-2) + n"<<endl;
	cout<<"Please Enter n = ";

	while(!(cin>>n))
	{
		cin.clear();
		cin.ignore(10000, '\n');

		cout<<"Expected Integer. Try Again."<<endl;
		cout<<"Please Enter n = ";
	}

	cout<<endl<<"u("<<n<<") = "<<n<<" * u("<<n<<"-1) + ("<<n<<"+1) * u("<<n<<"-2) +"<<n<<" = "<<u(n);

	return 0;
}
</source>
