<source lang="cpp">
//Exercise 13: Example 3

#include <iostream>

#define ERR_NONE	0;

using namespace std;

int main(int argc, char** argv)
{
	int iInput = 0;
	int iIndex = 0;
	int iCount = 0;
	int n = 0;

	cout << "Enter 10 integers:" << endl;		
	while(iIndex < 10)
	{
		cin >> iInput;

		if (iInput > n)
		{
			n=iInput;
			iCount=1;
		}
		else if (iInput == n)
		{
			iCount++;
		}
		iIndex++;
	};

	cout << "The occurance of the largest integer " << n << " is: " << iCount << endl;

return ERR_NONE;
}

