<source lang="cpp">
#include <iostream>
using namespace std;

int main ()
{
    int num[10], max, freq=0;
    cout << "Enter ten integers: " << endl;
    for (int i=0;i<=9;i++)
    {
        cin >> num[i];
    }
    max=num[0];
    for (int i=1;i<=9;i++)
    {
        if (num[i]>max) max=num[i];
    }
    for (int i=0;i<=9;i++)
    {
        if (num[i]==max)
        {
            num[i]=1;
            freq += num[i];
        }
    }
    cout << "The greatest value is " << max << ". Its frequency is " << freq << "." << endl;
    return 0;
}

