/*
EXERCISE 15
Write a program that asks the user to type an integer N 
and that indicates if N is a prime number or not.
*/

#include <iostream>

using namespace std;

void main() {
	int num, i;
	bool prime = true;
	cout << "Enter a number: ";
	cin >> num;
	for (i = num-1; i > 1 && prime == true; i--) {
		if (num%i == 0)
			prime = false;
	} 
	if (prime == true)
		cout << "Prime number";
	else
		cout << "Not prime number";
}
