Example 5:
<source lang="cpp">
#include <iostream>
#include <cmath>

#define ERR_NONE	0;

using namespace std;

bool isPrime(int n);

int main(int argc, char** argv)
{
	int n = 0;

	cout << "Enter an integer: ";
	cin >> n;		
	cout << n << " is " << (isPrime(abs((float)n)) ? "" : "NOT ") << "a prime number." << endl;

return ERR_NONE;
}

bool isPrime(int n)
{
	if (n > 1)
	{
		for (int i=(n-1);i>1;i--)
		{
			if(n%i == 0)
			{
				return false;
			}
		}
	}
	return true;
}
</source>

Solution #6
<source lang="cpp">
// by J0nDaFr3aK

#include <iostream>
using namespace std;

bool ifPrime(int n);

int main()
{
    int input;

    cout << "Enter a value: ";
    cin >> input;

    if (ifPrime(input))
       cout << input << " is prime!\n";
    else
       cout << input << " is not prime!\n";

    return 0;
}

bool ifPrime(int n) {
     int i;

     if (n == 2) return true;
     else if (n == 0 || n == 1 || n % 2 == 0) return false;
         else
             for (i = 3; i < (n / i); i+=2) {
                 if (n % i == 0)
                    return false;
             }
     return true;
}
</source>


Example 7

<source lang="cpp">
#include <iostream>
using namespace std;
int main() {
    start:
        int a,x;
        cout << "Enter a number: ";
        cin >> a;
        if (a%2==1 && a>1 || a==2) {
            for (x=3; x<=a/x; x+=2){            
                if (a%x==0)
                    goto no;              
                }
            goto yes;
            }
        else
            goto no;   
    yes:
        cout << a << " is a prime."<< endl;
        goto start;
    no:
        cout << a << " is not a prime." << endl;
        goto start;
    return 0;            
}
</source>

Solution #8
<source lang="cpp">

/*Using square root makes this a lot easier.
  You only have to check the numbers up to
  a numbers square root when looking for posible divisors.
*/

#include <iostream>
#include <cmath> //To be able to use sqrt()

using namespace std;

int main()
{
    cout << "Prime check" << endl;
    cout << "Enter an integer: ";

    bool isPrime = true;
    int value = 0;
    cin >> value;

    if (value < 2)
    	isPrime = false;

    //Only have to check up to the values square root.
    for (int i = 2; i <= sqrt(value) && isPrime; i++)
    {
 	if (value%i == 0)
            isPrime = false;
    }

    cout << "Entered value is" << (isPrime ? "" : " not") << " a prime number." << endl;
}
</source>

Solution #9  with recursive method
<source lang ="CPP">
#include <iostream>
#include <String>

using namespace std;

string isPrime (int in )
{
	static int i =2 ;
	if ( i < in )
	{
		if ( in % i == 0)
			return "Not Prime Number" ;
		else
		{
			i++;
			return isPrime (in); 
		}

	}
	return "Prime Number" ;
}

int main()
{
int in ;

cout << "Enter an integer : ";
cin >> in ;
cout << in << " is " << isPrime (in) << endl ;
	// wait for user response to exit
	system("pause");
	return 0;
}
