//Exercise 16
#include<iostream>
using namespace std;
      int input,loop[2],quo;
      int main(){
          cout<<"Enter a netural number ";
          cin>>input;
          cout<<"Prime number >=\t"<<input<<" are:"<<endl;
          for (loop[0]=1;loop[0]<=input;loop[0]++){
              for (loop[1]=2;loop[1]<loop[0];loop[1]++){
                  quo=loop[0]%loop[1];
                  if (quo==0) break;}
              if (quo!=0 ||loop[0]==1 ||loop[0]==2) cout<<loop[0]<<"\t";}
          cout<<endl;
          system("PAUSE");
          return 0;}
