<source lang="cpp">
#include<iostream>
using namespace std;

int main()
{
int N,i,nb=0,d;
bool is_prime;

cout<<"Typer the value of N : ";cin>>N;

for(i=2;i<=N;i++)
	{
	/* test if i is prime*/
	is_prime=true;
	d=2;
	while(is_prime && d*d<=i)
		if(i%d==0)is_prime=false; else d++;

	if(is_prime==true)nb++;
	}

cout<<"The number of prime number lesser or equal to "
	<<N<<" is "<<nb<<endl;

return 0;
}
