using namespace std;

int x;

int main () {
	cout << "Please enter a number and I will show you all prime numbers between that number and zero.\n";
	cin >> x;
	for ( int i = x; i > 1; --i) {
		if (i%2 != 0 && i%3 != 0 && i%5 != 0 && i%7!=0) 
			cout << i << ", ";
	}
	cout << "7, 5, 3, 2, and 1 are all prime numbers between 1 and " << x << ".";
}

