Solution #2
<source lang="cpp">
#include <iostream>
#include <cmath>

using namespace std;

bool isPrime(int n)
{
    for (int i = 2; i <= sqrt(n); i++)
    {
        if (n%i == 0)
	    return false;
    }
    return true;
}

int main()
{
    int count = 0,
    value = 0;

    cout << "Enter a value: ";
    cin >> value;

    int prime = 0;

    for (int i = 2; count < value; i++)
    {
	if(isPrime(i))
	{
	    count++;
 	    prime = i;
	}
    }

    cout << "The " << count << "th prime is " << prime << endl;
}
</source>
