<source lang="cpp">
/*
EXERCISE 18
Write a program that asks the user to type the value of N and writes this picture :
Example:

N=3
***
**
*

*/

#include <iostream>

using namespace std;

void main() {
	int N;
	cout << "Enter a number: ";
	cin >> N;
	cout << "\nN = " << N << endl;
	for (; N >= 1; N--) {
	for (int v(N); v != 0; v--) {
		cout << "*";
		}
		cout << endl;
	}
	cout << "Done.";
}

