      int input,loop[2];
      int main(){
          cout<<"Enter the number of star(s) :\t";
          cin>>input;
          cout<<endl<<endl;
          for (loop[0]=input;loop[0]>0;loop[0]--){
              for (loop[1]=0;loop[1]<(input-loop[0]);loop[1]++)
                  cout<<" ";
              for (loop[1]=1;loop[1]<=loop[0];loop[1]++)
                  cout<<"*";
              cout<<endl;}
          system("PAUSE");
          return 0;}
