// by blazzer12

#include <iostream>

using namespace std;

int main()
{
	int N;

	cout<<"N = ";

	while(!(cin>>N))
	{
		cin.clear();
		cin.ignore(10000,'\n');

		cout<<"Integer Expected. Try Again."<<endl;
		cout<<"N = ";
	}


	for(int space=0, star=N;(space<N && star>0); space++, star--)
	{
		for(int spc=space;spc>0;spc--)
		{
			cout<<" ";
		}
		for(int str=star;str>0; str--)
		{
			cout<<"*";
		}

		cout<<endl;
	}

	return 0;
}

</source>
