<source lang="cpp">
// by blazzer12

#include <iostream>

using namespace std;

int main()
{
	int N,spc,str;

	cout<<"N = ";

	while(!(cin>>N))
	{
		cin.clear();
		cin.ignore(10000,'\n');

		cout<<"Integer Expected. Try Again."<<endl;
		cout<<"N = ";
	}


	for(int space=0, star=N;(space<N && star>0); space++, star--)
	{
		spc=space;
		str=star;

		while(spc--)
		{
			cout<<" ";
		}
		while(str--)
		{
			cout<<"*";
		}

		cout<<endl;
	}

	return 0;
}

</source>
Solution #4
<source lang="cpp">
// blazzer12

#include <iostream>

using namespace std;

void generate(int space, int star)
{
	while(space--)
		cout<<" ";

	while(star--)
		cout<<"*";
}

int main()
{
	int N;
	cout<<"N = ";
	while(!(cin>>N))
	{
		cin.clear();
		cin.ignore(10000,'\n');

		cout<<"Integer Expected. Try Again."<<endl;
		cout<<"N = ";
	}

	for(int space=0, star=N; N--; space++, star--) // or the more standard: for(int space=0, star=N; N; space++, star--, N--)
	{
		generate(space, star);
		cout<<endl;
	}
	return 0;
}

</source>
