#include <iostream>
using namespace std;

int main() {
    for (int input; input != -999;) {
        cout << "Enter a number (-999 to quit): ";
        cin >> input;
        if (input != -999)
            cout << "Tripled: " << input * 3 << endl;
    }

    return(0);
}
