<source lang="cpp">
#include <iostream>

using namespace std;

int main() {
    int posCount(-1), input;

    do  {
        cout << "Enter a positive number (0 or less to quit): ";
        cin >> input;
        posCount++;
    } while (input > 0);

    cout << "Total number of positives: " << posCount;

    return(0);
}
</source>

