<source lang="cpp">
#include <iostream>
using namespace std;

int main() {

    float populationA = 50;
    float populationB = 70;
    int years = 0;
    while (populationA < populationB)
    {
        populationA += (3*populationA)/100;
        populationB += (2*populationB)/100;

        years++;
    }
    cout << "Population A: " << populationA << endl;
    cout << "Population B: " << populationB << endl;
    cout << "Years: " << years;

    return 0;
}

</source>

<source lang = "cpp">
#include <iostream>

float percent(float x,int p)
{
    return (x/100)*p;
}

int main()
{
    float A=50;
    float B=70;
    for(int i =0; A!=B;i++)
    {
        std::cout<<i<<"\n";
        A=A+percent(A,3);
        B=B+percent(B,7);
    }

}
