    for (i = 0; i < N; i++)
    {
        cout << "Type an integer: ";
        cin >> t[i];
    }

    cout << "Type the value of V: ";
    cin >> V;

    for (i = 0; i < N; i++)
    {
        if (t[i] == V)
        {
          cout << "V is in the array" << endl;
          return 0;
        }
    }

    cout << "V is not in the array" << endl;

    return 0;
}
