<source lang=cpp>
#include <iostream>
using namespace std;
void LargestElement(int arg[], int list) {
	int Index;
	int Largest = arg[0];

	for (n = 0; n < list; n++) {

		if (Largest < arg[n]) {
			Largest = arg[n];
			Index = n;
}
}
        cout << Largest << " " << Index << endl;
}
int main()
{
	int UserArray[10], input, x = 0;
        cout << "Please input 10 numbers: ";
	while (x < 10)
	{
                cin >> input;
		UserArray[x]=input;
		x++;
	}

	LargestElement(UserArray, 10);


return 0;

}
