<source lang="cpp">
// By J0nDaFr3aK
#include <iostream>
using namespace std;

#define LENGTH 10

int main()
{
    int array[LENGTH], v, i(-1), j, temp1, temp2;

    for (j = 0; j < LENGTH; j++) {
        cout << "enter number: ";
        cin >> array[j];
    }

    cout << "\nenter value of V: ";
    cin >> v;

    do {
        cout << "\nenter value of i (0 to " << LENGTH - 1 << "): ";
        cin >> i;
    } while (i < 0 || i > LENGTH - 1);

    // saves value to copy in next one before replacing it with V
    temp1 = array[i];
    array[i] = v;

    for (j = i + 1; j < LENGTH - 1; j++) {
        temp2 = array[j];
        array[j] = temp1;
        temp1 = temp2;        
    }

    array[j] = temp2; // j == LENGTH - 1 (9)

    // prints new values
    for (j = 0; j < LENGTH; j++) {
        cout << array[j] << " ";
    }

    return 0;
}
