// Assumes all elements are non-null and non-undefined
function insertionSort(sortMe) {
   for (var i = 0, j, tmp; i < sortMe.length; ++i) {
      tmp = sortMe[i];
      for (j = i - 1; j >= 0 && sortMe[j] > tmp; --j)
         sortMe[j + 1] = sortMe[j];
      sortMe[j + 1] = tmp;
   }
}
